const menuIcon=document.getElementById("menuIcon"),navbar=menuIcon.parentElement,logo=menuIcon.nextElementSibling,menu=navbar.lastElementChild,iconClose="./images/icon-close.svg",iconHamburger="./images/icon-hamburger.svg";function changeMenuIcon(){menu.classList.contains("js-show-menu")?menuIcon.setAttribute("src",iconClose):menuIcon.setAttribute("src",iconHamburger)}menuIcon.addEventListener("click",()=>{navbar.classList.toggle("js-open-menu"),logo.classList.toggle("js-hidden-logo"),menu.classList.toggle("js-show-menu"),changeMenuIcon()});